import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ServicioConsumoService } from 'src/app/service/servicio-consumo.service';
import { Router } from '@angular/router';
@Component({
  selector: 'app-empleados',
  templateUrl: './empleados.component.html',
  styleUrls: ['./empleados.component.css']
})
export class EmpleadosComponent {

  id!: any


get resultados(){
  console.log(this.service.resultados);
  return this.service.resultados;
}

constructor(public service:ServicioConsumoService, private http:HttpClient , private router:Router){
this.service.obtenerEmpleadosactivos();
}

DeleteEmpleado(id:any){
  const datos ={
    id : id,
    Estatus:false
  }

  let url="https://localhost:7054/api/Empleados/Delete"
  this.http.post(url,datos).toPromise().then(data=>{
    console.log(datos)
    console.log(data);
    window.location.reload()
  })

}


ActualizarEmpleado(id:any){

  this.router.navigate(['Empleados/Editar',id])
}


}

<br>


<div class="row">




    <div class="col-3" style="margin-bottom: 25px;" *ngFor="let TDC of resultados">

<div class="card" >
    <img src="data:image/png;base64,{{TDC.fotoEmpleado}}" class="card-img-top" alt="Empleado">

    <div class="card-body">
      <h5 class="card-title">{{TDC.nombreEmpleado}}</h5>
      <p class="card-text">{{TDC.apellidoPaternoEmpleado}} {{TDC.apellidoMaternoEmpleado}}</p>
    </div>
    <ul class="list-group list-group-flush">
      <li class="list-group-item">{{TDC.puestoTrabajo}}</li>
      <li class="list-group-item">{{TDC.salario}}</li>
      <li class="list-group-item">A third item</li>
    </ul>
    <div class="card-body">
      <button (click)="DeleteEmpleado(TDC.id)" class="card-link">Eliminar</button>
      <a (click)="ActualizarEmpleado(TDC.id)" class="card-link">Edicion</a>
    </div>
  </div>
    </div>
</div>
