import { Component } from '@angular/core';
import { ServicioConsumoService } from 'src/app/service/servicio-consumo.service';
@Component({
  selector: 'app-empleados',
  templateUrl: './empleados.component.html',
  styleUrls: ['./empleados.component.css']
})
export class EmpleadosComponent {


get resultados(){
  console.log(this.service.resultados);
  return this.service.resultados;
}

constructor(public service:ServicioConsumoService){
this.service.obtenerEmpleadosactivos();
}
}

<br>


<div class="row">




    <div class="col-3" style="margin-bottom: 25px;" *ngFor="let TDC of resultados">

<div class="card" >
    <img src="data:image/pnp" class="card-img-top" alt="Empleado">

    <div class="card-body">
      <h5 class="card-title">{{TDC.nombreEmpleado}}</h5>
      <p class="card-text">{{TDC.apellidoPaternoEmpleado}} {{TDC.apellidoMaternoEmpleado}}</p>
    </div>
    <ul class="list-group list-group-flush">
      <li class="list-group-item">{{TDC.puestoTrabajo}}</li>
      <li class="list-group-item">{{TDC.salario}}</li>
      <li class="list-group-item">A third item</li>
    </ul>
    <div class="card-body">
      <a href="#" class="card-link">Agregar beneficiario</a>
      <a href="#" class="card-link">Another link</a>
    </div>
  </div>
    </div>
</div>
